item.description:
 &lt;div style=&#34;font-size: 14px; margin: 0; padding: 0; width: 100%;&#34;&gt;&lt;p style=&#34;line-height: 160%; box-sizing: content-box; margin: 10px 0; color: #333;&#34;&gt;SpaceVim&amp;#x4E3A;&lt;a href=&#34;https://www.gnu.org/software/global/globaldoc_toc.html&#34; style=&#34;line-height: 160%; box-sizing: content-box; text-decoration: underline; color: #5286bc;&#34;&gt;gtags&lt;/a&gt;&amp;#x548C;&lt;a href=&#34;http://cscope.sourceforge.net/&#34; style=&#34;line-height: 160%; box-sizing: content-box; text-decoration: underline; color: #5286bc;&#34;&gt;cscope&lt;/a&gt;&amp;#x5206;&amp;#x522B;&amp;#x63D0;&amp;#x4F9B;&amp;#x4E86;&amp;#x72EC;&amp;#x7ACB;&amp;#x7684;layer&amp;#x3002;&amp;#x672C;&amp;#x7BC7;&amp;#x91CD;&amp;#x70B9;&amp;#x4ECB;&amp;#x7ECD;&amp;#x4E00;&amp;#x4E0B;&amp;#x8FD9;&amp;#x4E24;&amp;#x4E2A;layer&amp;#x7684;&amp;#x4F7F;&amp;#x7528;&amp;#x601D;&amp;#x8DEF;&amp;#x548C;&amp;#x65B9;&amp;#x6CD5;&amp;#xFF0C;&amp;#x4EE5;&amp;#x53CA;&amp;#x73B0;&amp;#x5728;&amp;#x7684;bug&amp;#x3002;&amp;#x5148;&amp;#x8BF4;&amp;#x7ED3;&amp;#x8BBA;&amp;#xFF0C;&amp;#x867D;&amp;#x7136;&amp;#x6211;&amp;#x6253;&amp;#x7B97;migrate&amp;#x5230;SpaceVim&amp;#x4E86;&amp;#xFF0C;&amp;#x4F46;&amp;#x662F;&amp;#x6211;&amp;#x6253;&amp;#x7B97;&amp;#x653E;&amp;#x5F03;&amp;#x8FD9;&amp;#x4FE9;layer&amp;#xFF0C;&amp;#x56E0;&amp;#x4E3A;bug&amp;#x7684;&amp;#x95EE;&amp;#x9898;&amp;#xFF0C;&amp;#x5BFC;&amp;#x81F4;&amp;#x4E0D;&amp;#x53EF;&amp;#x7528;&amp;#x3002;&amp;#x6240;&amp;#x4EE5;&amp;#x8FD8;&amp;#x662F;&amp;#x81EA;&amp;#x884C;&amp;#x914D;&amp;#x7F6E;&amp;#x63D2;&amp;#x4EF6;&amp;#x6765;&amp;#x4F7F;&amp;#x7528;&amp;#x5427;&amp;#x3002;&lt;/p&gt;
&lt;h2 style=&#34;line-height: 160%; box-sizing: content-box; font-weight: 700; font-size: 34px; border-bottom: 1px solid #dbdbdb; color: #333;&#34;&gt;gtags&lt;/h2&gt;
&lt;p style=&#34;line-height: 160%; box-sizing: content-box; margin: 10px 0; color: #333;&#34;&gt;&amp;#x5728;&amp;#x4F7F;&amp;#x7528;SpaceVim&amp;#x4E4B;&amp;#x524D;&amp;#xFF0C;&amp;#x6CA1;&amp;#x6709;&amp;#x542C;&amp;#x8BF4;&amp;#x8FC7;gtags+global&amp;#x8FD9;&amp;#x4E00;&amp;#x5957;tag&amp;#x67E5;&amp;#x8BE2;&amp;#x5DE5;&amp;#x5177;&amp;#x3002;&amp;#x4E5F;&amp;#x662F;&amp;#x770B;&amp;#x4E86;SpaceVim gtags layer&amp;#x624D;&amp;#x60F3;&amp;#x8BD5;&amp;#x8BD5;&amp;#x770B;&amp;#x3002;&lt;/p&gt;
&lt;h3 style=&#34;line-height: 160%; box-sizing: content-box; font-weight: 700; font-size: 27px; color: #333;&#34;&gt;&amp;#x547D;&amp;#x4EE4;&amp;#x884C;&amp;#x4F7F;&amp;#x7528;gtags+globa&lt;/h3&gt;
&lt;p style=&#34;line-height: 160%; box-sizing: content-box; margin: 10px 0; color: #333;&#34;&gt;&amp;#x5982;&amp;#x679C;&amp;#x662F;&amp;#x5728;&amp;#x547D;&amp;#x4EE4;&amp;#x884C;&amp;#x4F7F;&amp;#x7528;gtags&amp;#xFF0C;&amp;#x9700;&amp;#x8981;&amp;#xFF1A;&lt;/p&gt;
&lt;ul style=&#34;line-height: 160%; box-sizing: content-box; display: block; list-style-type: disc; padding-left: 30px; margin: 6px 0 10px; color: #333;&#34;&gt;
