&lt;li style=&#34;line-height: 160%; box-sizing: content-box; position: relative;&#34;&gt;&amp;#x7136;&amp;#x540E;&amp;#x5C31;&amp;#x53EF;&amp;#x4EE5;&amp;#x5728;&amp;#x9879;&amp;#x76EE;&amp;#x7684;&amp;#x4EFB;&amp;#x4F55;&amp;#x4F4D;&amp;#x7F6E;&amp;#xFF0C;&amp;#x4F7F;&amp;#x7528;global&amp;#x547D;&amp;#x4EE4;&amp;#x6765;&amp;#x67E5;&amp;#x8BE2;&amp;#x4F60;&amp;#x9700;&amp;#x8981;&amp;#x7684;&amp;#x4FE1;&amp;#x606F;&amp;#x3002;&lt;/li&gt;
