&lt;h3 style=&#34;line-height: 160%; box-sizing: content-box; font-weight: 700; font-size: 27px; color: #333;&#34;&gt;cscope&amp;#x60F3;&amp;#x505A;&amp;#x5230;&amp;#x7684;&lt;/h3&gt;
&lt;p style=&#34;line-height: 160%; box-sizing: content-box; margin: 10px 0; color: #333;&#34;&gt;&amp;#x901A;&amp;#x8FC7;&amp;#x7406;&amp;#x89E3;cscope.vim&amp;#x6E90;&amp;#x7801;&amp;#xFF0C;&amp;#x53EF;&amp;#x4EE5;&amp;#x5927;&amp;#x6982;&amp;#x4E86;&amp;#x89E3;&amp;#x8FD9;&amp;#x4E2A;layer&amp;#x60F3;&amp;#x505A;&amp;#x7684;&amp;#x601D;&amp;#x8DEF;&amp;#x3002;&amp;#x57FA;&amp;#x672C;&amp;#x4E0A;&amp;#x548C;gtags&amp;#x601D;&amp;#x8DEF;&amp;#x7C7B;&amp;#x4F3C;&amp;#xFF0C;&amp;#x5C31;&amp;#x662F;&amp;#x5728;&amp;#x6E90;&amp;#x7801;&amp;#x6811;&amp;#x610F;&amp;#x5916;&amp;#x7684;&amp;#x76EE;&amp;#x5F55;&amp;#x4E0B;&amp;#x751F;&amp;#x6210;cscope.out&amp;#x6587;&amp;#x4EF6;&amp;#x5E76;&amp;#x901A;&amp;#x8FC7;cscope add&amp;#x547D;&amp;#x4EE4;&amp;#x6765;&amp;#x6DFB;&amp;#x52A0;&amp;#x3002;&lt;/p&gt;
&lt;pre style=&#34;line-height: 160%; box-sizing: content-box; border: 0; border-radius: 0; margin: 2px 0 8px; background-color: #f5f7f8;&#34;&gt;&lt;code style=&#34;display: block; overflow-x: auto; background: #1e1e1e; line-height: 160%; box-sizing: content-box; border: 0; border-radius: 0; letter-spacing: -.3px; padding: 18px; color: #f4f4f4; white-space: pre-wrap;&#34;&gt;function! cscope#preloadDB()
  let dirs = split(g:cscope_preload_path, s:FILE.pathSeparator)
  for m_dir in dirs
    let m_dir = s:CheckAbsolutePath(m_dir, m_dir)
    if !has_key(s:dbs, m_dir)
      call s:InitDB(m_dir)
    endif
    call s:LoadDB(m_dir)
  endfor
endfunction
&lt;/code&gt;&lt;/pre&gt;
